(13, 'align=', '[align=left ou center ou right]TEXTE[/align] Aligner du texte', 1, '[align={TEXT1}]{TEXT2}[/align]', '<div style="text-align: {TEXT1};">{TEXT2}</div>', '!\\[align\\=(.*?)\\](.*?)\\[/align\\]!ies', '''[align=''.str_replace(array("\\r\\n", ''\\"'', ''\\'''', ''('', '')''), array("\\n", ''"'', ''&#39;'', ''&#40;'', ''&#41;''), trim(''${1}'')).'':$uid]''.str_replace(array("\\r\\n", ''\\"'', ''\\'''', ''('', '')''), array("\\n", ''"'', ''&#39;'', ''&#40;'', ''&#41;''), trim(''${2}'')).''[/align:$uid]''', '!\\[align\\=(.*?):$uid\\](.*?)\\[/align:$uid\\]!s', '<div style="text-align: ${1};">${2}</div>'),
(14, 'spoiler', '[spoiler]Texte à cacher[/spoiler]', 1, '[spoiler]{TEXT}[/spoiler]', '<div style="margin:20px;margin-top:5px"><div class="quotetitle"><input type="button" value="Afficher" style="width:60px;font-size:10px;margin:0px;padding:0px;" onclick="if(this.parentNode.parentNode.getElementsByTagName(''div'')[1].getElementsByTagName(''div'')[0].style.display!=''''){this.parentNode.parentNode.getElementsByTagName(''div'')[1].getElementsByTagName(''div'')[0].style.display='''';this.innerText='''';this.value=''Masquer'';}else{this.parentNode.parentNode.getElementsByTagName(''div'')[1].getElementsByTagName(''div'')[0].style.display=''none'';this.innerText='''';this.value=''Afficher'';}" /></div><div class="quotecontent"><div style="display:none;">{TEXT}</div></div></div>', '!\\[spoiler\\](.*?)\\[/spoiler\\]!ies', '''[spoiler:$uid]''.str_replace(array("\\r\\n", ''\\"'', ''\\'''', ''('', '')''), array("\\n", ''"'', ''&#39;'', ''&#40;'', ''&#41;''), trim(''${1}'')).''[/spoiler:$uid]''', '!\\[spoiler:$uid\\](.*?)\\[/spoiler:$uid\\]!s', '<div style="margin:20px;margin-top:5px"><div class="quotetitle"><input type="button" value="Afficher" style="width:60px;font-size:10px;margin:0px;padding:0px;" onclick="if(this.parentNode.parentNode.getElementsByTagName(''div'')[1].getElementsByTagName(''div'')[0].style.display!=''''){this.parentNode.parentNode.getElementsByTagName(''div'')[1].getElementsByTagName(''div'')[0].style.display='''';this.innerText='''';this.value=''Masquer'';}else{this.parentNode.parentNode.getElementsByTagName(''div'')[1].getElementsByTagName(''div'')[0].style.display=''none'';this.innerText='''';this.value=''Afficher'';}" /></div><div class="quotecontent"><div style="display:none;">${1}</div></div></div>'),
(15, 'barre', 'Texte barré', 1, '[barre]{TEXT}[/barre]', '<span style="text-decoration:line-through;">{TEXT}</span>', '!\\[barre\\](.*?)\\[/barre\\]!ies', '''[barre:$uid]''.str_replace(array("\\r\\n", ''\\"'', ''\\'''', ''('', '')''), array("\\n", ''"'', ''&#39;'', ''&#40;'', ''&#41;''), trim(''${1}'')).''[/barre:$uid]''', '!\\[barre:$uid\\](.*?)\\[/barre:$uid\\]!s', '<span style="text-decoration:line-through;">${1}</span>'),
(16, 'clignote', 'Texte qui clignote', 1, '[clignote]{TEXT}[/clignote]', '<script type="text/javascript">\n/*\nSCRIPT DE L''EDITEUR JAVASCRIPT\nhttp://www.editeurjavascript.com\n*/\ngo_visibility = new Array;\n\nfunction goblink()\n   {\n   if(document.getElementById && document.all)\n      {\n      blink_tab = document.getElementsByTagName(''blink'');\n      for(a=0;a<blink_tab.length;a++)\n         {\n         if(go_visibility[a] != "visible")\n            go_visibility[a] = "visible";\n         else\n            go_visibility[a] = "hidden";\n         blink_tab[a].style.visibility=go_visibility[a];\n         }\n      }\n   setTimeout("goblink()", 500);\n   }\n\nwindow.onload = goblink;\n</script>\n<blink>{TEXT}</blink>', '!\\[clignote\\](.*?)\\[/clignote\\]!ies', '''[clignote:$uid]''.str_replace(array("\\r\\n", ''\\"'', ''\\'''', ''('', '')''), array("\\n", ''"'', ''&#39;'', ''&#40;'', ''&#41;''), trim(''${1}'')).''[/clignote:$uid]''', '!\\[clignote:$uid\\](.*?)\\[/clignote:$uid\\]!s', '<script type="text/javascript">\n/*\nSCRIPT DE L''EDITEUR JAVASCRIPT\nhttp://www.editeurjavascript.com\n*/\ngo_visibility = new Array;\n\nfunction goblink()\n   {\n   if(document.getElementById && document.all)\n      {\n      blink_tab = document.getElementsByTagName(''blink'');\n      for(a=0;a<blink_tab.length;a++)\n         {\n         if(go_visibility[a] != "visible")\n            go_visibility[a] = "visible";\n         else\n            go_visibility[a] = "hidden";\n         blink_tab[a].style.visibility=go_visibility[a];\n         }\n      }\n   setTimeout("goblink()", 500);\n   }\n\nwindow.onload = goblink;\n</script>\n<blink>${1}</blink>'),
(17, 'defil', 'Faire défiler du texte', 1, '[defil]{TEXT}[/defil]', '<marquee>{TEXT}</marquee>', '!\\[defil\\](.*?)\\[/defil\\]!ies', '''[defil:$uid]''.str_replace(array("\\r\\n", ''\\"'', ''\\'''', ''('', '')''), array("\\n", ''"'', ''&#39;'', ''&#40;'', ''&#41;''), trim(''${1}'')).''[/defil:$uid]''', '!\\[defil:$uid\\](.*?)\\[/defil:$uid\\]!s', '<marquee>${1}</marquee>'),
(18, 'defilement=', '[defilement={vitesse},{left ou right}]{le texte à faire défiler}[/defilement]', 1, '[defilement={NUMBER},{TEXT}]{TEXT1}[/defilement]', '<MARQUEE SCROLLAMOUNT="{NUMBER}" DIRECTION="{TEXT}" BGCOLOR="#e1ebf2" TEXT="black">\n{TEXT1}\n</MARQUEE>', '!\\[defilement\\=([0-9]+),(.*?)\\](.*?)\\[/defilement\\]!ies', '''[defilement=${1},''.str_replace(array("\\r\\n", ''\\"'', ''\\'''', ''('', '')''), array("\\n", ''"'', ''&#39;'', ''&#40;'', ''&#41;''), trim(''${2}'')).'':$uid]''.str_replace(array("\\r\\n", ''\\"'', ''\\'''', ''('', '')''), array("\\n", ''"'', ''&#39;'', ''&#40;'', ''&#41;''), trim(''${3}'')).''[/defilement:$uid]''', '!\\[defilement\\=([0-9]+),(.*?):$uid\\](.*?)\\[/defilement:$uid\\]!s', '<MARQUEE SCROLLAMOUNT="${1}" DIRECTION="${2}" BGCOLOR="#e1ebf2" TEXT="black">\n${3}\n</MARQUEE>'),
